import { useEffect, useCallback, useReducer } from "react";
import Meals from "../MealItem/Meals";
import { DUMMY_MEALS } from "../../../dummyMeals";

const intialReducerObj = {
  meals: {
    id: "",
    name: "Sushi",
    description: "Best quality food",
    price: 22.99,
  },

  loading: false,
  error: false,
};

const mealsReducer = (state, action) => {

  switch (action.type) {
    case "LOADING":
      return {
        ...state,
        ...state.meals,
        loading: action.payload,
      };

    case "MEALS":
      return {
        meals: {
          id: action.payload.id,
          name: action.payload.name,
          description: action.payload.description,
          price: action.payload.price,
        },
      };


    case "ERROR":
      return {
        ...state,
        ...state.meals,
        error: action.payload,
      };

    default:
      return intialReducerObj;
  }
};
const MealsList = () => {



  const [state, dispatch] = useReducer(mealsReducer, intialReducerObj);

  useEffect(() => {

    const getIntialMeals = async () => {

      try {
        dispatch({
          type: "LOADING",
          payload: true,
        });

        const req = await fetch(
          "https://food-app-a635f-default-rtdb.firebaseio.com/meals.json"
        );

        if (!req.ok) {
          throw new Error("Something went wrong");
        }

        const res = await req.json();

        let loadedMeals = [];

        for (const key in res) {
          loadedMeals.push({
            id: key,
            name: res[key].name,
            price: res[key].price,
            description: res[key].description,
          });



          dispatch({
            type: "SET-MEALS",
            payload: loadedMeals,
          });



        }
      } catch (error) {
        dispatch({
          type: "ERROR",
          payload: error.message,
        });


      }
      dispatch({
        type: "LOADING",
        payload: false,
      });

    }

    getIntialMeals()



  }, []);

 

  return (
    <div>
      {state.loading && <p>Loading...</p>}
      {state.error && <p> {state.error.message} </p>}
      {console.log(state.meals)}
      {state.meals.map((meals) => {
        return <Meals key={meals.id} props={meals} />;
      })}
     

    </div>
  );
};

export default MealsList;
