import React from 'react'
import useInput from '../../../customHooks/useInput'
import { emailValidator, nameInputValidator } from "../../../UtilityFunctions/inputValidationFunctions"
const OrderForm = (props) => {
    const formIsValid = false

    const {
        value: enteredNameValue,
        hasError: nameInputError,
        valueChangedHandler: nameChangeHandler,
        InputBlurHandler: nameInputBlurHandler,
        isValid: valueIsValid,
        reset: resetInput
    } = useInput(emailValidator)

    const {
        value: enteredEmailValue,
        hasError: emailInputError,
        valueChangedHandler: emailChangeHandler,
        InputBlurHandler: emailInputBlurHandler,
        isValid: emailIsValid,
        reset: resetEmail
    } = useInput(nameInputValidator)

    return (
        <form onSubmit={""}>
            <label value="Name"> Name</label>
            <br/>
            <input
                placeholder="Name"
                type='text'
                id='name'
                value={enteredNameValue}
                onChange={nameChangeHandler}
                onBlur={nameInputBlurHandler} />
            <br />
            <input
                placeholder="Email"
                type='email'
                id='email'
                value={enteredEmailValue}
                onChange={emailChangeHandler}
                onBlur={emailInputBlurHandler} />
            {nameInputError && <p className={"error-text"}> Cannot be empty </p>}
        {emailInputError && <p className={"error-text"}> Does not include "@" </p>}
    
        </form>
    )
}

export default OrderForm
