import React, { useContext, useState, useEffect, useRef } from 'react'
import classes from './Cart.module.css'
import Modal from '../UI/Modal/Modal'
import CartContext from '../../contextStore/CartContext'
import CartItem from './CartItem/CartItem'
import OrderForm from '../UI/Forms/OrderForm'

const Cart = (props) => {

    const cartCTX = useContext(CartContext)
    const totalAmount = `$${cartCTX.totalAmount.toFixed(2)}`
    const hasItems = cartCTX.items.length > 0;

    const [orderInfo, setOrderInfo] = useState({})
    const [order, setOrder] = useState({})

    const [success, setSuccess] = useState(false)
    const [valid, setValid] = useState(false)



    const requestConfig = (order) => {
        return {
            method: "POST",
            headers: {},
            body: JSON.stringify(order)
        }

    }

    const sendOrderInfo = async (order) => {
        try {
            const res = await fetch("https://food-app-a635f-default-rtdb.firebaseio.com/order.json", requestConfig(order))

            if (!res.ok) {
                throw new Error("Request Failed")
            }
            // const data = await res.json()
            setSuccess(true)

        } catch (error) {
            setSuccess(false)
        }
    }

    const nameHander = (obj) => {

       
        setOrderInfo(obj)
    //     const { name, email, city, postalCode } = orderInfo
    
    
    //             const order = {
    //                 name,
    //                 email,
    //                 city,
    //                 postalCode,
    //                 items: cartCTX.items,
    //                 totalAmount: cartCTX.totalAmount
    //             }
    //             setOrder(order)
    }

    const orderCartItems = () => {

        console.log(order)
        sendOrderInfo(order)
       
    }



    // useEffect(() => {

    //     const orderFull = () => {
    //         const { name, email, city, postalCode } = orderInfo
    
    
    //         const order = {
    //             name,
    //             email,
    //             city,
    //             postalCode,
    //             items: cartCTX.items,
    //             totalAmount: cartCTX.totalAmount
    //         }
    //         setOrder(order)
    //     }

    //     if (valid) {
    //         orderFull()
           
    //     }

    // }, [valid, cartCTX, orderInfo ])


 


    const validityChecker = (value) => {

        setValid(value)


    }


    const cartItemRemoveHandler = (id) => {
        cartCTX.removeItem(id)

    }
    const cartItemAddHandler = (item) => {
        cartCTX.addItem({ ...item, amount: 1 })
    }

    const cartItems = <ul className={classes[`cart-items`]}>
        {cartCTX.items.map(item => <CartItem
            key={item.id}
            name={item.name}
            price={item.price}
            amount={item.amount}
            onRemove={cartItemRemoveHandler.bind(null, item.id)}
            onAdd={cartItemAddHandler.bind(null, item)} />)}
    </ul>


    return (
        <>



            {success ? <Modal> Success </Modal> : <Modal onClose={props.onHideCart}>
                {cartItems}

                {hasItems && <OrderForm
                    valid={validityChecker}
                    name={nameHander}
                />}


                <div className={classes.total}>
                    <span>Total Amount</span>
                    <span>{totalAmount}</span>
                </div>

                <div className={classes.actions}>
                    <button className={classes[`button--alt`]} onClick={props.onHideCart}> Close</button>
                    {hasItems && valid && <button
                        className={classes.button}
                        onClick={orderCartItems}> Order</button>}


                </div>



            </Modal>}
        </>
    )
}

export default Cart
