import React, { useReducer } from "react";
import OrderContext from "./CartContext";

const defaultOrderState = {
  items: [],
  totalAmount: 0,
};

const cartReducer = (state, action) => {
  switch (action.type) {
    case "CREATE_ORDER":
        return action.payload;
    
        
        default: 
      }
   
};

const OrderProvider = (props) => {
  const [orderState, dispOrderAction] = useReducer(cartReducer, defaultOrderState);

  const addItem = (item) => {
    dispOrderAction({
      type: "ADD_ITEM",
      payload: item,
    });
  };

  const createOrder = (order) => {
    dispOrderAction({
          type: "CREATE_ORDER",
          payload: order,
      })
  }

  const removeItem = (id) => {
    dispOrderAction({
      type: "REMOVE_ITEM",
      payload: id,
    });
  };

  const orderContext = {
    name: orderState.name,
    email: orderState.email,
    city: orderState.city,
    postalCode: orderState.postalCode,
    items: orderState.items,
    totalAmount: orderState.totalAmount,
    addItem: addItem,
    removeItem: removeItem,
    createOrder: createOrder,
  };
  return (
    <OrderContext.Provider value={orderContext}>
      {props.children}
    </OrderContext.Provider>
  );
};

export default OrderProvider;
