import { useEffect, useCallback, useReducer } from "react";
import Meals from "../MealItem/Meals";
import { DUMMY_MEALS } from "../../../dummyMeals";

const intialReducerObj = {
  meals: {
    id: "",
    name: "Sushi",
    description: "Best quality food",
    price: 22.99,
  },

  loading: false,
  loaded: false,
  error: false,

}

const mealsReducer = (state, action) => {

  switch (action.type) {
    case "LOADING":
      return {
        loading: action.payload
      }

    case "SET-MOVIE":

      return {
        meals: {
          id: action.payload.id,
          name: action.payload.name,
          description: action.payload.description,
          price: action.payload.price,
        }
      }

    case "LOADED": {
      return {
        loaded: true
      }
    }
    case "ERROR":
      return {
        error: action.payload,
      }


    default:
      return intialReducerObj
  }
}
const MealsList = () => {



  const getIntialMeals = useCallback(async () => {
    try {
      dispatch({
        type: 'LOADING',
        payload: true
      })

      const req = await fetch("https://food-app-a635f-default-rtdb.firebaseio.com/meals.json")

      if (!req.ok) {
        throw new Error("Something went wrong"),
        dispatch({
          type: 'ERROR',
          payload:  new Error("Something went wrong"),
        })
      }

      const res = await req.json()

      let loadedMeals = []

      for (const key in res) {
        loadedMeals.push({
          id: key,
          name: res[key].name,
          price: res[key].price,
          description: res[key].description,
        })

        dispatch({
          type: 'LOADED',
          payload: true
        })

        dispatch({
          type: 'setMovie',
          payload: loadedMeals
        })
      }

     

    } catch (error) {
      dispatch({
        type: 'ERROR',
        payload: error.message,
      })

    }
    dispatch({
      type: 'LOADING',
      payload: false
    })
  }, [])

  const [state, dispatch] = useReducer(mealsReducer, getIntialMeals)

  useEffect(() => {
    
    getIntialMeals()
    return () => {
      return
    }
  }, [getIntialMeals])
  console.log(state)

  return (
    <div>
      {state.error && <p> {state.error} </p>}
      {console.log(state.meals)}
      {state.loaded && state.meals !==undefined && state.meals.map((meals) => {
        return (
          <Meals key={meals.id} props={state.meals} />
        )
      })}
    </div>
  );
};

export default MealsList;
